import React, {Component} from "react"
import "../../menu.css"

export default class Main extends Component {
  state = {

  }

  // sanitizeMultiples = (keysArr, item) =>{
  //   keysArr.forEach((key, index) => {
  //     if (Array.isArray(item[key])) {
  //       this.setState(prevState => ({
  //         ...prevState,
  //         [`set${index}`]: 
  //       }))
  //     }
  //   })
  // }

  render() {
    // let keysArr = Object.keys(this.props.item)
    // this.sanitizeMultiples(keysArr, this.props.item)
    let dots = this.props.item.style_type ? "dots-right-section" : "dots-right"
    return (
      <div className="menu-item d-flex mb-2">
        <div className="flex-row dots">
          <span className="content dots-left">
            {this.item.item_name}
          </span> 
          {
            this.item.pieces &&
            <span className="">({this.item.pieces}pcs)</span>
          }     
          <span className={`price ml-auto ml-auto ${dots}`}>${this.item.price}</span>
        </div>
        {
          this.item.details &&
          <div className="item-detail">({this.item.details})</div>
        }
        {
          this.item.ingredients &&
          <div className="item-detail">{this.item.ingredients}</div>
        }

      </div>
    )
  }
}
