import React, { Component } from "react"
import Drinks from "./MenuSections/Drinks"
import Appetizers from "./MenuSections/Appetizers"
import Mains from "./MenuSections/Mains"
import Desserts from "./MenuSections/Desserts"

//this is just a conceptual outline, may need to adjust going forward

class Menu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            drinks: false,
            appetizers: false,
            mains: false,
            desserts: false
        }
    }

    onClick = (e) => {
        console.log(e.currentTarget.name) //check to sync component to state
        const activeMenu = e.currentTarget.name
        if (this.state.activeMenu) {
            this.setState({
                [activeMenu]: false
            })
        } else {
            this.setState({
                [activeMenu]: true
            })
        }
    }

    render() { 
        console.log(this.state)
        return (
            <div>
                <div className="drink-banner"
                     name="drinks"
                     onClick={this.onClick}>Drinks
                </div>
                {this.state.drinks && <Drinks/>}
                <div className="appetizer-banner"
                     name="appetizers"
                     onClick={this.onClick}>Appetizers
                </div>
                {this.state.appetizers && <Appetizers/>}
                <div className="main-banner"
                     name="mains"
                     onClick={this.onClick}>Mains
                </div>
                {this.state.mains && <Mains/>}
                <div className="dessert-banner">Desserts</div>
                {this.state.desserts && <Desserts name="desserts" onClick={this.onClick}/>}      
            </div>
        );
    }
}

export default Menu;
